mapper.select:
select su.companyId,su.deptId,su.id,su.realName,sd.`name` as deptName ,sum(case when fe.businessType = 2 then floor(fe.amount/100) end) as sellIncome ,sum(case when fe.businessType = 3 then floor(fe.amount/100) end) as rentIncome ,sum(case when fe.businessType = 1 then floor(fe.amount/100) end) as newHouseIncome ,sum(case when fe.businessType = 4 then floor(fe.amount/100) end) as financeIncome ,(sum(case when fe.businessType = 2 then floor(fe.amount/100) end) +sum(case when fe.businessType = 3 then floor(fe.amount/100) end) +sum(case when fe.businessType = 1 then floor(fe.amount/100) end) +sum(case when fe.businessType = 4 then floor(fe.amount/100) end)) as incomeSum
from sys_user su left join sys_dept sd on su.deptId = sd.id left join finance_employeeCommissions fe on fe.userId=su.id and fe.months='2018-10' and fe.isValid=1 where su.companyId=#{companyId}
and su.id = #{userId}
group by su.id order by incomeSum desc

		select companyId,deptId,userId,sellIncome,rentIncome,newHouseIncome,financeIncome,(sellIncome+rentIncome+newHouseIncome+financeIncome) as incomeSum from anal_incomestatistics where companyId=#{companyId} and months='2018-10' 

   			and userId = #{userId}

		ORDER BY incomeSum desc
