mapper.select:
SELECT dc.communityId,d.id,d.`name`,dc.communityName,COUNT(DISTINCT sh1.id) AS storeHouse,COUNT(DISTINCT sh2.id) AS totalHouse,TRUNCATE(COUNT(DISTINCT sh1.id)/COUNT(DISTINCT sh2.id),2) AS storeHouseRate, COUNT(DISTINCT dt1.id)AS storeSellDeal,COUNT(DISTINCT dt2.id) AS sellTotalDeal, TRUNCATE(COUNT(DISTINCT dt1.id)/COUNT(DISTINCT dt2.id),2) AS storeSellDealRate, (COUNT(DISTINCT dt2.id)-COUNT(DISTINCT dt1.id)) AS storeSellOtherDeal, (COUNT(DISTINCT dt3.id)-COUNT(DISTINCT dt1.id)) AS sellOtherDeal, COUNT(DISTINCT dt4.id)AS storeRentDeal,COUNT(DISTINCT dt5.id) AS rentTotalDeal,TRUNCATE(COUNT(DISTINCT dt4.id)/COUNT(DISTINCT dt5.id),2) AS storeRentDealRate, (COUNT(DISTINCT dt5.id)-COUNT(DISTINCT dt4.id)) AS storeRentOtherDeal, (COUNT(DISTINCT dt6.id)-COUNT(DISTINCT dt4.id)) AS rentOtherDeal FROM sys_dept_responsibility_community AS dc INNER JOIN sys_dept AS d ON d.isvalid=1 AND dc.isValid=1 AND d.id=dc.deptId AND d.companyId=#{companyId}
and d.id=#{deptId}
LEFT JOIN hs_housebaseinfo AS hb ON hb.communityId=dc.communityId LEFT JOIN hs_secondhandhouse AS sh1 ON sh1.houseId=hb.id AND sh1.isValid=1 AND sh1.mainterDeptId=d.id AND sh1.companyId=#{companyId} AND sh1.openPlateDate BETWEEN #{beginDate} AND concat(#{endDate},' 23:59:59') LEFT JOIN hs_secondhandhouse AS sh2 ON sh2.houseId=hb.id AND sh2.isValid=1 AND sh2.companyId=#{companyId} AND sh2.openPlateDate BETWEEN #{beginDate} AND concat(#{endDate},' 23:59:59') LEFT JOIN hs_secondhandhouse AS u ON u.mainterDeptId=d.id AND u.companyId=#{companyId} LEFT JOIN deal_transactioninfo AS dt1 ON dt1.communityId=dc.communityId AND dt1.isValid=1 AND dt1.dealType='00' AND dt1.shhid=u.id AND dt1.dealTime BETWEEN #{beginDate} AND concat(#{endDate},' 23:59:59') LEFT JOIN deal_transactioninfo AS dt2 ON dt2.communityId=dc.communityId AND dt2.isValid=1 AND dt2.dealType='00' AND dt2.companyId=#{companyId} AND dt2.dealTime BETWEEN #{beginDate} AND concat(#{endDate},' 23:59:59') LEFT JOIN deal_transactioninfo AS dt3 ON dt3.communityId=dc.communityId AND dt3.isValid=1 AND dt3.dealType='00' AND dt3.dealTime BETWEEN #{beginDate} AND concat(#{endDate},' 23:59:59') LEFT JOIN deal_transactioninfo AS dt4 ON dt4.communityId=dc.communityId AND dt4.isValid=1 AND dt4.dealType='01' AND dt4.shhid=u.id AND dt4.dealTime BETWEEN #{beginDate} AND concat(#{endDate},' 23:59:59') LEFT JOIN deal_transactioninfo AS dt5 ON dt5.communityId=dc.communityId AND dt5.isValid=1 AND dt5.dealType='01' AND dt5.companyId=#{companyId} AND dt5.dealTime BETWEEN #{beginDate} AND concat(#{endDate},' 23:59:59') LEFT JOIN deal_transactioninfo AS dt6 ON dt6.communityId=dc.communityId AND dt6.isValid=1 AND dt6.dealType='01' AND dt6.dealTime BETWEEN #{beginDate} AND concat(#{endDate},' 23:59:59') GROUP BY dc.communityId ORDER BY d.id
