mapper.select:
  		SELECT ${columns} FROM ${tableName} 




				#{item.key} like concat('%',concat(#{item.value},'%'))


				#{item.key} between #{item.value1} and #{item.value2}


				#{item.key}  #{item.symbol}  #{item.value}

select (select max(FORMAT((length(id)-length(companyId))/2,0)+1) as maxLevel from sys_dept where isvalid = 1) as maxLevel,id as code,name,parentId as parentCode,FORMAT((length(id)-length(companyId))/2,0) as level from sys_dept WHERE isValid = 1
AND companyId=#{companyId}
union all select (select max(FORMAT((length(id)-length(companyId))/2,0) +1) from sys_dept where isvalid=1) as maxLevel, a.id as code,a.realName as name,b.id as parentCode,(select max(FORMAT((length(id)-length(companyId))/2,0)+1) from sys_dept where isvalid=1) as level from sys_user a left join sys_dept b on a.deptId = b.id where a.isValid = 1 and b.isvalid = 1
AND a.companyId=#{companyId}
